import React from "react";

// Create a new component named "Math"
// Render one Math component in the place of each "?" mark
// Math should accept 3 props
// num1, operator, and num2
// Math should return a span tag displaying the result e.g.  19 + 341 = 360
const Math = ({ num1, num2, operator }) => {
    let newAns = 0

    if (operator === "+") {
        newAns = {num1} + {num2}
        return (
    <span> {num1} + {num2} = {newAns} </span>)}

    else if (operator === "-") {
        newAns = {num1} - {num2}
        return (
    <span> {num1} - {num2} = {newAns} </span>)}

    else if (operator === "*") {
        newAns = {num1} * {num2}
        return (
    <span> {num1} * {num2} = {newAns} </span>)}

    else if (operator === "/") {
        newAns = {num1} / {num2}
        return (
    <span> {num1} / {num2} = {newAns} </span>)}
}

export default Math;
